import { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Alert,
  Table,
  Badge,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  InputGroup,
  InputGroupText,
  ButtonGroup,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  Progress,
} from "reactstrap";
import Header from "components/Headers/Header.js";

// Import with error handling for optional dependencies
let XLSX, jsPDF;
try {
  XLSX = require('xlsx');
  jsPDF = require('jspdf');
  require('jspdf-autotable');
} catch (error) {
  console.warn("Export dependencies not available:", error.message);
}

const EvenementForm = () => {
  const [formData, setFormData] = useState({
    psDateDebut: "",
    psDateFin: "",
    psStatus: "",
    psDescription: "",
    aTitle: "",
    aLocalisation: ""
  });

  const [alert, setAlert] = useState({ visible: false, message: "", color: "" });
  const [evenements, setEvenements] = useState([]);
  const [filteredEvenements, setFilteredEvenements] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [addModal, setAddModal] = useState(false);
  const [editingEvenement, setEditingEvenement] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  // AI States
  const [aiInput, setAiInput] = useState("");
  const [aiResponse, setAiResponse] = useState(null);
  const [isAiLoading, setIsAiLoading] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);

  // Stats & Export States
  const [stats, setStats] = useState(null);
  const [activeTab, setActiveTab] = useState('1');
  const [exportLoading, setExportLoading] = useState(false);
  const [statsLoading, setStatsLoading] = useState(false);

  // New AI Features States
  const [generatedArticles, setGeneratedArticles] = useState([]);
  const [attendancePredictions, setAttendancePredictions] = useState(null);
  const [articleTopic, setArticleTopic] = useState("");
  const [articleType, setArticleType] = useState("Standard");
  const [isGeneratingArticle, setIsGeneratingArticle] = useState(false);
  const [isPredictingAttendance, setIsPredictingAttendance] = useState(false);
  const [autoSaveArticles, setAutoSaveArticles] = useState(false);

  // Helper functions
  const showAlert = (message, color) => {
    setAlert({ visible: true, message, color });
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'planifiÃ©':
      case 'planifie':
        return "info";
      case 'en cours':
        return "warning";
      case 'terminÃ©':
      case 'termine':
      case 'complÃ©tÃ©':
      case 'complete':
        return "success";
      case 'annulÃ©':
      case 'annule':
        return "danger";
      default:
        return "secondary";
    }
  };

  const getStatusIcon = (status) => {
    switch (status?.toLowerCase()) {
      case 'planifiÃ©':
      case 'planifie':
        return "ðŸ“…";
