import React, { useState } from "react";
import { getArticles, addArticle, deleteArticle, updateArticle } from "../services/articleService";

export default function Articles() {
  const [articles, setArticles] = useState(getArticles());
  const [form, setForm] = useState({
    aNom: "",
    aDescriptionArc: "",
    aTypePai: "",
    aImageArct: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newArt = addArticle(form);
    setArticles([...articles, newArt]);
    setForm({
      aNom: "",
      aDescriptionArc: "",
      aTypePai: "",
      aImageArct: ""
    });
  };

  const handleDelete = (id) => {
    deleteArticle(id);
    setArticles(getArticles());
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-bold mb-3">Gestion des Articles</h2>
      <form onSubmit={handleSubmit} className="space-y-2 mb-4">
        <input name="aNom" placeholder="Nom" value={form.aNom} onChange={handleChange} className="form-control" />
        <input name="aDescriptionArc" placeholder="Description" value={form.aDescriptionArc} onChange={handleChange} className="form-control" />
        <input name="aTypePai" placeholder="Type de Paiement" value={form.aTypePai} onChange={handleChange} className="form-control" />
        <input name="aImageArct" placeholder="Image (URL)" value={form.aImageArct} onChange={handleChange} className="form-control" />
        <button type="submit" className="btn btn-primary">Ajouter</button>
      </form>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
            <th>Type Paiement</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {articles.map((a) => (
            <tr key={a.id}>
              <td>{a.aNom}</td>
              <td>{a.aDescriptionArc}</td>
              <td>{a.aTypePai}</td>
              <td><img src={a.aImageArct} alt={a.aNom} width="50" /></td>
              <td>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(a.id)}>Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
