import React, { useState } from 'react';
import PaymentForm from './PaymentForm';
import PaymentsList from './PaymentsList';
import PaymentStats from './PaymentStats';

/**
 * Page complète de gestion des paiements
 * Combine le formulaire, la liste et les statistiques
 */
export default function PaymentsPage() {
  const [currentUser, setCurrentUser] = useState('UtilisateuR');
  const [refreshKey, setRefreshKey] = useState(0);

  // Fonction pour rafraîchir la liste après création/modification
  const handlePaymentSaved = () => {
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div style={{ padding: 20, maxWidth: 1400, margin: '0 auto' }}>
      <h1 style={{ marginBottom: 30 }}>Gestion des Paiements</h1>

      {/* Sélecteur d'utilisateur */}
      <div style={{ marginBottom: 30 }}>
        <label style={{ marginRight: 10, fontWeight: 'bold' }}>
          Utilisateur:
        </label>
        <select 
          value={currentUser} 
          onChange={e => setCurrentUser(e.target.value)}
          style={{ padding: 8, fontSize: 16 }}
        >
          <option value="Utilisateur_John">John</option>
          <option value="Utilisateur_Jane">Jane</option>
          <option value="Utilisateur_Bob">Bob</option>
        </select>
      </div>

      {/* Statistiques */}
      <PaymentStats user={currentUser} key={`stats-${refreshKey}`} />

      {/* Formulaire de création */}
      <div style={{
        background: '#f8f9fa',
        padding: 20,
        borderRadius: 8,
        marginBottom: 30
      }}>
        <PaymentForm 
          defaultUser={currentUser}
          onSaved={handlePaymentSaved}
        />
      </div>

      {/* Liste des paiements */}
      <PaymentsList 
        user={currentUser} 
        refreshTrigger={refreshKey}
      />
    </div>
  );
}
