import React, { useState, useEffect } from 'react';
import { createPayment, getServices } from '../api';
import {
  Card,
  CardHeader,
  CardBody,
  Form,
  FormGroup,
  Input,
  Button,
  Label,
  Badge,
  Row,
  Col
} from 'reactstrap';

const PAYMENT_MODES = ['Carte', 'Esp√®ces', 'Virement', 'Ch√®que', 'Assurance'];
const PAYMENT_STATUS = ['PAID', 'PENDING', 'FAILED', 'REFUNDED'];

export default function PaymentForm({ onSaved, defaultUser }) {
  const [user, setUser] = useState(defaultUser || 'Utilisateur_John');
  const [service, setService] = useState('');
  const [montant, setMontant] = useState('');
  const [modePaiement, setModePaiement] = useState('Carte');
  const [status, setStatus] = useState('PAID');
  const [referenceFacture, setReferenceFacture] = useState('');
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [services, setServices] = useState([]);
  const [loadingServices, setLoadingServices] = useState(true);

  const loadServices = async () => {
    setLoadingServices(true);
    try {
      const data = await getServices();
      setServices(data);
      if (data.length > 0 && !service) {
        setService(data[0].id);
      }
    } catch (err) {
      console.error('Erreur chargement services:', err);
      alert('Erreur lors du chargement des services: ' + err.message);
    } finally {
      setLoadingServices(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user || !service || !montant) {
      alert('‚ö†Ô∏è Veuillez remplir tous les champs obligatoires');
      return;
    }
    setLoading(true);
    try {
      await createPayment({
        user,
        service,
        montant: parseFloat(montant),
        modePaiement,
        status,
        referenceFacture: referenceFacture || undefined,
        date
      });
      // Reset form
      setMontant('');
      setReferenceFacture('');
      setDate(new Date().toISOString().slice(0, 10));
      if (onSaved) onSaved();
      alert('‚úÖ Paiement cr√©√© avec succ√®s!');
    } catch (err) {
      alert('‚ùå Erreur: ' + err.message);
    } finally {
      setLoading(false);
    }
  }

  const getStatusColor = (status) => {
    switch(status) {
      case 'PAID': return 'success';
      case 'PENDING': return 'warning';
      case 'FAILED': return 'danger';
      case 'REFUNDED': return 'info';
      default: return 'secondary';
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'PAID': return '‚úÖ';
      case 'PENDING': return '‚è≥';
      case 'FAILED': return '‚ùå';
      case 'REFUNDED': return '‚Ü©Ô∏è';
      default: return 'üìã';
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
      <h3>Nouveau Paiement</h3>

      <div style={{ marginBottom: 10 }}>
        <label>Utilisateur *</label>
        <input 
          value={user} 
          onChange={e => setUser(e.target.value)} 
          placeholder="Utilisateur_John"
          required
        />
      </div>

      <div style={{ marginBottom: 10 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 5 }}>
          <label style={{ margin: 0 }}>
            Service * {services.length > 0 && `(${services.length} disponible${services.length > 1 ? 's' : ''})`}
          </label>
          <button 
            type="button" 
            onClick={loadServices} 
            disabled={loadingServices}
            style={{ 
              padding: '4px 8px', 
              fontSize: '12px', 
              cursor: loadingServices ? 'not-allowed' : 'pointer',
              opacity: loadingServices ? 0.6 : 1
            }}
          >
            üîÑ Actualiser
          </button>
        </div>
        <select value={service} onChange={e => setService(e.target.value)} required disabled={loadingServices}>
          <option value="">
            {loadingServices ? '‚è≥ Chargement...' : '-- S√©lectionner un service --'}
          </option>
          {services.map(s => (
            <option key={s.id} value={s.id}>
              [{s.type}] {s.label || s.id}
            </option>
          ))}
        </select>
        {!loadingServices && services.length === 0 && (
          <small style={{ color: '#888', display: 'block', marginTop: 5 }}>
            ‚ö†Ô∏è Aucun service disponible. Cr√©ez-en un d'abord dans la page Services.
          </small>
        )}
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Montant (‚Ç¨) *</label>
        <input 
          type="number" 
          step="0.01" 
          value={montant} 
          onChange={e => setMontant(e.target.value)} 
          placeholder="80.00"
          required
        />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Mode de paiement</label>
        <select value={modePaiement} onChange={e => setModePaiement(e.target.value)}>
          {PAYMENT_MODES.map(mode => (
            <option key={mode} value={mode}>{mode}</option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Statut</label>
        <select value={status} onChange={e => setStatus(e.target.value)}>
          {PAYMENT_STATUS.map(st => (
            <option key={st} value={st}>{st}</option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Date</label>
        <input 
          type="date" 
          value={date} 
          onChange={e => setDate(e.target.value)} 
        />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>R√©f√©rence facture (optionnel)</label>
        <input 
          value={referenceFacture} 
          onChange={e => setReferenceFacture(e.target.value)} 
          placeholder="F2025-001"
        />
      </div>

      <button type="submit">Cr√©er le paiement</button>
    </form>
  );
}
