import React, { useState, useEffect } from 'react';
import HabitudeForm from './HabitudeForm';
import HabitudeItem from './HabitudeItem';
import RechercheHabitudes from './RechercheHabitudes';
// Use the existing exports instead of gestionnaireHabitudes
import { getAllHabitudes, createHabitude, updateHabitude, deleteHabitude } from '../../api/habitudeApi';

const HabitudeList = () => {
  const [habitudes, setHabitudes] = useState([]);
  const [filteredHabitudes, setFilteredHabitudes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingHabitude, setEditingHabitude] = useState(null);

  useEffect(() => {
    loadHabitudes();
  }, []);

  const loadHabitudes = async () => {
    try {
      setLoading(true);
      const response = await getAllHabitudes(); // Use direct export
      setHabitudes(response.data);
      setFilteredHabitudes(response.data);
    } catch (err) {
      setError('Erreur lors du chargement des habitudes');
      console.error('Error loading habitudes:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (searchTerm) => {
    if (!searchTerm.trim()) {
      setFilteredHabitudes(habitudes);
      return;
    }
    
    const filtered = habitudes.filter(habitude =>
      habitude.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      habitude.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      habitude.categorie?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredHabitudes(filtered);
  };

  const handleCreateHabitude = async (habitudeData) => {
    try {
      await createHabitude(habitudeData); // Use direct export
      await loadHabitudes();
      setShowForm(false);
    } catch (err) {
      setError('Erreur lors de la création de l\'habitude');
      console.error('Error creating habitude:', err);
    }
  };

  const handleUpdateHabitude = async (id, habitudeData) => {
    try {
      await updateHabitude(id, habitudeData); // Use direct export
      await loadHabitudes();
      setEditingHabitude(null);
    } catch (err) {
      setError('Erreur lors de la mise à jour de l\'habitude');
      console.error('Error updating habitude:', err);
    }
  };

  const handleDeleteHabitude = async (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer cette habitude ?')) {
      try {
        await deleteHabitude(id); // Use direct export
        await loadHabitudes();
      } catch (err) {
        setError('Erreur lors de la suppression de l\'habitude');
        console.error('Error deleting habitude:', err);
      }
    }
  };

  const handleEdit = (habitude) => {
    setEditingHabitude(habitude);
    setShowForm(true);
  };

  if (loading) return <div className="loading">Chargement des habitudes...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="habitude-list">
      <div className="habitude-header">
        <h2>Gestion des Habitudes</h2>
        <button 
          className="btn-primary"
          onClick={() => {
            setEditingHabitude(null);
            setShowForm(true);
          }}
        >
          Ajouter une habitude
        </button>
      </div>

      <RechercheHabitudes onSearch={handleSearch} />

      {showForm && (
        <HabitudeForm
          habitude={editingHabitude}
          onSubmit={editingHabitude ? 
            (data) => handleUpdateHabitude(editingHabitude._id, data) : 
            handleCreateHabitude
          }
          onCancel={() => {
            setShowForm(false);
            setEditingHabitude(null);
          }}
        />
      )}

      <div className="habitudes-grid">
        {filteredHabitudes.length === 0 ? (
          <div className="no-results">
            Aucune habitude trouvée
          </div>
        ) : (
          filteredHabitudes.map(habitude => (
            <HabitudeItem
              key={habitude._id}
              habitude={habitude}
              onEdit={() => handleEdit(habitude)}
              onDelete={() => handleDeleteHabitude(habitude._id)}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default HabitudeList;
