import React, { useState } from 'react';
import { createService } from '../api';

// types possibles (fixés par ton ontologie)
const SERVICE_TYPES = ['Consultation','Analyse','Telemedecine'];

export default function ServiceForm({ onSaved }) {
  const [type, setType] = useState('Consultation');
  const [id, setId] = useState('');
  const [label, setLabel] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await createService({ id: id || undefined, type, label });
      setId(''); setLabel('');
      if (onSaved) onSaved();
      alert('Service créé');
    } catch (err) {
      alert('Erreur: ' + err.message);
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <h3>Nouvel élément service</h3>
      <div>
        <label>Type</label>
        <select value={type} onChange={e => setType(e.target.value)}>
          {SERVICE_TYPES.map(t => <option key={t}>{t}</option>)}
        </select>
      </div>
      <div>
        <label>ID (optionnel)</label>
        <input value={id} onChange={e=>setId(e.target.value)} placeholder="Consultation_002" />
      </div>
      <div>
        <label>Label</label>
        <input value={label} onChange={e=>setLabel(e.target.value)} placeholder="Consultation initiale"/>
      </div>
      <button type="submit">Créer</button>
    </form>
  );
}
