    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      // Envoyer la requ√™te au backend Saif AI
      const response = await axios.post('/api/saif-ai/executer', {
        prompt: inputMessage,
        context: context,
        executeQuery: true
