sourcesContent:
import { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Alert,
  Table,
  Badge,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  InputGroup,
  InputGroupText,
  ButtonGroup,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
} from "reactstrap";
import Header from "components/Headers/Header.js";

// Import with error handling for optional dependencies
let XLSX, jsPDF;
try {
  XLSX = require('xlsx');
  jsPDF = require('jspdf');
  require('jspdf-autotable');
} catch (error) {
  console.warn("Export dependencies not available:", error.message);
}

const ArticleForm = () => {
  const [formData, setFormData] = useState({
    aNom: "",
    aDescriptionArc: "",
    aTypePai: "",
    aImageArct: "",
    evenementLie: ""
  });
  const [alert, setAlert] = useState({ visible: false, message: "", color: "" });
  const [articles, setArticles] = useState([]);
  const [filteredArticles, setFilteredArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [addModal, setAddModal] = useState(false);
  const [editingArticle, setEditingArticle] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [evenementsDisponibles, setEvenementsDisponibles] = useState([]);

  // AI States
  const [aiInput, setAiInput] = useState("");
  const [aiResponse, setAiResponse] = useState(null);
  const [isAiLoading, setIsAiLoading] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);

  // Stats & Export States
  const [stats, setStats] = useState(null);
  const [activeTab, setActiveTab] = useState('1');
  const [exportLoading, setExportLoading] = useState(false);
  const [statsLoading, setStatsLoading] = useState(false);

  useEffect(() => {
    fetchArticles();
    fetchEvenementsDisponibles();
    fetchStats();
  }, []);

  useEffect(() => {
    filterArticles();
  }, [searchTerm, articles, sortConfig, activeFilters]);

  const fetchArticles = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/articles');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      console.log("ðŸ“¦ DonnÃ©es articles reÃ§ues:", result);
      setArticles(result.results.bindings || []);
    } catch (error) {
      console.error("âŒ Erreur fetch articles:", error);
      showAlert("Erreur lors du chargement des articles: " + error.message, "danger");
    } finally {
      setLoading(false);
    }
  };

  const fetchEvenementsDisponibles = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/articles/evenements/disponibles');
      if (response.ok) {
        const result = await response.json();
        console.log("ðŸ“‹ Ã‰vÃ©nements disponibles reÃ§us:", result.evenements);
        setEvenementsDisponibles(result.evenements || []);
      }
    } catch (error) {
      console.error("âŒ Erreur fetch Ã©vÃ©nements:", error);
    }
  };

  const fetchStats = async () => {
    setStatsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/ai/stats/articles');
      if (response.ok) {
        const statsData = await response.json();
        setStats(statsData);
      } else {
        calculateStatsFromData();
      }
    } catch (error) {
      console.error("âŒ Erreur stats:", error);
      calculateStatsFromData();
    } finally {
      setStatsLoading(false);
    }
  };

  const calculateStatsFromData = () => {
    if (articles.length === 0) {
      setStats({
        types: [],
        relations: { withEvent: 0, withoutEvent: 0, total: 0 }
      });
      return;
    }

    // Calculate type stats
    const typeCounts = {};
