import React, { useState } from "react";
import { getEvenements, addEvenement, deleteEvenement, updateEvenement } from ".";

export default function Evenements() {
  const [evenements, setEvenements] = useState(getEvenements());
  const [form, setForm] = useState({
    psDateDébut: "",
    psDateFin: "",
    psStatus: "",
    psDescription: "",
    aTitle: "",
    aLocalisation: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newEvt = addEvenement(form);
    setEvenements([...evenements, newEvt]);
    setForm({
      psDateDébut: "",
      psDateFin: "",
      psStatus: "",
      psDescription: "",
      aTitle: "",
      aLocalisation: ""
    });
  };

  const handleDelete = (id) => {
    deleteEvenement(id);
    setEvenements(getEvenements());
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-bold mb-3">Gestion des Événements</h2>
      <form onSubmit={handleSubmit} className="space-y-2 mb-4">
        <input name="aTitle" placeholder="Titre" value={form.aTitle} onChange={handleChange} className="form-control" />
        <input name="aLocalisation" placeholder="Localisation" value={form.aLocalisation} onChange={handleChange} className="form-control" />
        <input name="psDateDébut" type="date" value={form.psDateDébut} onChange={handleChange} className="form-control" />
        <input name="psDateFin" type="date" value={form.psDateFin} onChange={handleChange} className="form-control" />
        <input name="psStatus" placeholder="Statut" value={form.psStatus} onChange={handleChange} className="form-control" />
        <textarea name="psDescription" placeholder="Description" value={form.psDescription} onChange={handleChange} className="form-control" />
        <button type="submit" className="btn btn-primary">Ajouter</button>
      </form>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Titre</th>
            <th>Localisation</th>
            <th>Début</th>
            <th>Fin</th>
            <th>Statut</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {evenements.map((e) => (
            <tr key={e.id}>
              <td>{e.aTitle}</td>
              <td>{e.aLocalisation}</td>
              <td>{e.psDateDébut}</td>
              <td>{e.psDateFin}</td>
              <td>{e.psStatus}</td>
              <td>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(e.id)}>Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
