import React, { useEffect, useState } from 'react';
import { getServices, deleteService } from '../api';
import EditServiceModal from './EditServiceModal';

export default function ServicesList({ onSelect }) {
  const [services, setServices] = useState([]);
  const [editing, setEditing] = useState(null);

  async function load() {
    try {
      const data = await getServices();
      setServices(data);
    } catch (e) {
      console.error(e);
      alert('Erreur récup services: ' + e.message);
    }
  }

  useEffect(() => { load(); }, []);

  async function handleDelete(s) {
    if (!window.confirm('Supprimer ce service ?')) return;
    const id = s.id || s.uri.split(/[#\/]/).pop();
    await deleteService(id);
    load();
  }

  // group by type for UI clarity
  const byType = services.reduce((acc, s) => {
    acc[s.type = s.type || 'Unknown'] = acc[s.type] || [];
    acc[s.type].push(s);
    return acc;
  }, {});

  return (
    <div>
      <h2>Services</h2>
      {Object.keys(byType).map(type => (
        <div key={type} style={{marginBottom:12}}>
          <h3>{type}</h3>
          <ul>
            {byType[type].map(s => (
              <li key={s.id || s.uri}>
                <strong>{s.id}</strong>{s.label ? ` — ${s.label}` : ''} {' '}
                <button onClick={() => setEditing(s)}>Modifier</button>{' '}
                <button onClick={() => handleDelete(s)}>Supprimer</button>{' '}
                {onSelect && <button onClick={()=>onSelect(s)}>Choisir</button>}
              </li>
            ))}
          </ul>
        </div>
      ))}
      {editing && <EditServiceModal service={editing} onClose={() => { setEditing(null); load(); }} />}
    </div>
  );
}
