import { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Alert,
  Table,
  Badge,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  InputGroup,
  InputGroupText,
} from "reactstrap";
import Header from "components/Headers/Header.js";

const ScoreSanteCRUD = () => {
  const [formData, setFormData] = useState({
    scoreActivite: "",
    scoreGlobale: "",
    scoreNutrition: "",
    scoreSommeil: ""
  });
  const [alert, setAlert] = useState({ visible: false, message: "", color: "" });
  const [scores, setScores] = useState([]);
  const [filteredScores, setFilteredScores] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [editingScore, setEditingScore] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  useEffect(() => {
    fetchScores();
  }, []);

  useEffect(() => {
    filterScores();
  }, [searchTerm, scores]);

  const fetchScores = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/scores-sante');
      const result = await response.json();
      setScores(result.results.bindings);
    } catch (error) {
      showAlert("Erreur lors du chargement des scores", "danger");
    } finally {
      setLoading(false);
    }
  };

  const filterScores = () => {
    let filtered = scores.filter(score => {
      const activite = score.activite.value;
      const globale = score.globale.value;
      const nutrition = score.nutrition.value;
      const sommeil = score.sommeil.value;
      const id = score.score.value.split('#')[1];

      const searchLower = searchTerm.toLowerCase();
      return (
        activite.toString().includes(searchLower) ||
        globale.toString().includes(searchLower) ||
        nutrition.toString().includes(searchLower) ||
        sommeil.toString().includes(searchLower) ||
        id.toLowerCase().includes(searchLower)
      );
    });

    // Appliquer le tri après le filtrage
    if (sortConfig.key) {
      filtered.sort((a, b) => {
        let aValue = getSortValue(a, sortConfig.key);
        let bValue = getSortValue(b, sortConfig.key);

        if (aValue < bValue) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }

    setFilteredScores(filtered);
  };

  const getSortValue = (score, key) => {
    switch (key) {
      case 'activite':
        return parseInt(score.activite.value);
      case 'globale':
        return parseInt(score.globale.value);
      case 'nutrition':
        return parseInt(score.nutrition.value);
      case 'sommeil':
        return parseInt(score.sommeil.value);
      case 'id':
        return score.score.value.split('#')[1];
      default:
        return '';
    }
  };

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const getSortIcon = (key) => {
    if (sortConfig.key !== key) return '↕️';
    return sortConfig.direction === 'asc' ? '↑' : '↓';
  };

  const showAlert = (message, color) => {
    setAlert({ visible: true, message, color });
    setTimeout(() => setAlert({ ...alert, visible: false }), 5000);
  };

  const onDismiss = () => setAlert({ ...alert, visible: false });

  // CREATE
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/scores-sante', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          scoreActivite: formData.scoreActivite,
          scoreGlobale: formData.scoreGlobale,
          scoreNutrition: formData.scoreNutrition,
          scoreSommeil: formData.scoreSommeil
        })
      });

      if (response.ok) {
        showAlert("Score santé ajouté avec succès!", "success");
        setFormData({ 
          scoreActivite: "", 
          scoreGlobale: "", 
          scoreNutrition: "", 
          scoreSommeil: "" 
        });
        fetchScores();
      } else {
        throw new Error('Erreur lors de l\'insertion');
      }
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  // UPDATE - Ouvrir modal d'édition
  const openEditModal = (score) => {
    setEditingScore(score);
    setFormData({
      scoreActivite: score.activite.value,
      scoreGlobale: score.globale.value,
      scoreNutrition: score.nutrition.value,
      scoreSommeil: score.sommeil.value
    });
    setEditModal(true);
  };

  // UPDATE - Sauvegarder
  const handleUpdate = async (e) => {
    e.preventDefault();
    
    try {
      const scoreId = editingScore.score.value.split('#')[1];
      const response = await fetch(`http://localhost:5000/api/scores-sante/${scoreId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          scoreActivite: formData.scoreActivite,
          scoreGlobale: formData.scoreGlobale,
          scoreNutrition: formData.scoreNutrition,
          scoreSommeil: formData.scoreSommeil
        })
      });

      if (response.ok) {
        showAlert("Score santé modifié avec succès!", "success");
        setEditModal(false);
        setEditingScore(null);
        setFormData({ scoreActivite: "", scoreGlobale: "", scoreNutrition: "", scoreSommeil: "" });
        fetchScores();
      } else {
        throw new Error('Erreur lors de la modification');
      }
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  // DELETE
  const handleDelete = async (scoreUri) => {
    if (window.confirm("Êtes-vous sûr de vouloir supprimer ce score santé ?")) {
      try {
        const scoreId = scoreUri.split('#')[1];
        const response = await fetch(`http://localhost:5000/api/scores-sante/${scoreId}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          showAlert("Score santé supprimé avec succès!", "success");
          fetchScores();
        } else {
          throw new Error('Erreur lors de la suppression');
        }
      } catch (error) {
        showAlert("Erreur: " + error.message, "danger");
      }
    }
  };

  const handleChange = (e) => {
    const value = Math.min(100, Math.max(0, e.target.value));
    setFormData({
      ...formData,
      [e.target.name]: value
    });
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const getScoreColor = (score) => {
    if (score >= 80) return "success";
    if (score >= 60) return "warning";
    return "danger";
  };

  const getScoreEvaluation = (score) => {
    if (score >= 80) return "Excellent";
    if (score >= 60) return "Bon";
    return "À améliorer";
  };

  return (
    <>
      <Header />
      <Container className="mt--7" fluid>
        <Row>
          <Col lg="6">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <h3 className="mb-0">Gestion des Scores Santé</h3>
              </CardHeader>
              <CardBody>
                {alert.visible && (
                  <Alert color={alert.color} toggle={onDismiss}>
                    {alert.message}
                  </Alert>
                )}
                
                <Form onSubmit={handleSubmit}>
                  <FormGroup>
                    <Label for="scoreActivite">Score Activité (0-100)</Label>
                    <Input
                      type="number"
                      min="0"
                      max="100"
                      name="scoreActivite"
                      id="scoreActivite"
                      placeholder="Ex: 85"
                      value={formData.scoreActivite}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="scoreGlobale">Score Globale (0-100)</Label>
                    <Input
                      type="number"
                      min="0"
                      max="100"
                      name="scoreGlobale"
                      id="scoreGlobale"
                      placeholder="Ex: 78"
                      value={formData.scoreGlobale}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="scoreNutrition">Score Nutrition (0-100)</Label>
                    <Input
                      type="number"
                      min="0"
                      max="100"
                      name="scoreNutrition"
                      id="scoreNutrition"
                      placeholder="Ex: 80"
                      value={formData.scoreNutrition}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="scoreSommeil">Score Sommeil (0-100)</Label>
                    <Input
                      type="number"
                      min="0"
                      max="100"
                      name="scoreSommeil"
                      id="scoreSommeil"
                      placeholder="Ex: 75"
                      value={formData.scoreSommeil}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <Button color="primary" type="submit">
                    Ajouter le Score Santé
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>

          {/* TABLEAU DES SCORES */}
          <Col lg="6">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <h3 className="mb-0">Liste des Scores Santé</h3>
                <div className="d-flex justify-content-between align-items-center">
                  <Button color="primary" size="sm" onClick={fetchScores}>
                    Actualiser
                  </Button>
                  <span className="text-muted">
                    {filteredScores.length} / {scores.length} résultats
                  </span>
                </div>
              </CardHeader>
              <CardBody>
                {/* BARRE DE RECHERCHE */}
                <InputGroup className="mb-3">
                  <InputGroupText>🔍</InputGroupText>
                  <Input
                    placeholder="Rechercher par score, ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                  />
                </InputGroup>

                {loading ? (
                  <div>Chargement...</div>
                ) : (
                  <Table responsive striped>
                    <thead className="thead-light">
                      <tr>
                        <th onClick={() => handleSort('id')} style={{ cursor: 'pointer' }}>
                          ID {getSortIcon('id')}
                        </th>
                        <th onClick={() => handleSort('activite')} style={{ cursor: 'pointer' }}>
                          Activité {getSortIcon('activite')}
                        </th>
                        <th onClick={() => handleSort('globale')} style={{ cursor: 'pointer' }}>
                          Globale {getSortIcon('globale')}
                        </th>
                        <th onClick={() => handleSort('nutrition')} style={{ cursor: 'pointer' }}>
                          Nutrition {getSortIcon('nutrition')}
                        </th>
                        <th onClick={() => handleSort('sommeil')} style={{ cursor: 'pointer' }}>
                          Sommeil {getSortIcon('sommeil')}
                        </th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredScores.length === 0 ? (
                        <tr>
                          <td colSpan="6" className="text-center text-muted">
                            {scores.length === 0 ? 'Aucun score trouvé' : 'Aucun résultat pour votre recherche'}
                          </td>
                        </tr>
                      ) : (
                        filteredScores.map((score, index) => (
                          <tr key={index}>
                            <td>
                              <Badge color="info">
                                {score.score.value.split('#')[1]}
                              </Badge>
                            </td>
                            <td>
                              <div>
                                <Badge color={getScoreColor(parseInt(score.activite.value))}>
                                  {score.activite.value}
                                </Badge>
                                <br />
                                <small className="text-muted">
                                  {getScoreEvaluation(parseInt(score.activite.value))}
                                </small>
                              </div>
                            </td>
                            <td>
                              <div>
                                <Badge color={getScoreColor(parseInt(score.globale.value))}>
                                  {score.globale.value}
                                </Badge>
                                <br />
                                <small className="text-muted">
                                  {getScoreEvaluation(parseInt(score.globale.value))}
                                </small>
                              </div>
                            </td>
                            <td>
                              <div>
                                <Badge color={getScoreColor(parseInt(score.nutrition.value))}>
                                  {score.nutrition.value}
                                </Badge>
                                <br />
                                <small className="text-muted">
                                  {getScoreEvaluation(parseInt(score.nutrition.value))}
                                </small>
                              </div>
                            </td>
                            <td>
                              <div>
                                <Badge color={getScoreColor(parseInt(score.sommeil.value))}>
                                  {score.sommeil.value}
                                </Badge>
                                <br />
                                <small className="text-muted">
                                  {getScoreEvaluation(parseInt(score.sommeil.value))}
                                </small>
                              </div>
                            </td>
                            <td>
                              <Button 
                                color="warning" 
                                size="sm" 
                                onClick={() => openEditModal(score)}
                              >
                                ✏️
                              </Button>
                              {' '}
                              <Button 
                                color="danger" 
                                size="sm" 
                                onClick={() => handleDelete(score.score.value)}
                              >
                                🗑️
                              </Button>
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </Table>
                )}
              </CardBody>
            </Card>
          </Col>
        </Row>

        {/* MODAL D'ÉDITION */}
        <Modal isOpen={editModal} toggle={() => setEditModal(!editModal)}>
          <ModalHeader toggle={() => setEditModal(!editModal)}>
            Modifier le Score Santé
          </ModalHeader>
          <Form onSubmit={handleUpdate}>
            <ModalBody>
              <FormGroup>
                <Label for="edit-scoreActivite">Score Activité</Label>
                <Input
                  type="number"
                  min="0"
                  max="100"
                  name="scoreActivite"
                  id="edit-scoreActivite"
                  value={formData.scoreActivite}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="edit-scoreGlobale">Score Globale</Label>
                <Input
                  type="number"
                  min="0"
                  max="100"
                  name="scoreGlobale"
                  id="edit-scoreGlobale"
                  value={formData.scoreGlobale}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="edit-scoreNutrition">Score Nutrition</Label>
                <Input
                  type="number"
                  min="0"
                  max="100"
                  name="scoreNutrition"
                  id="edit-scoreNutrition"
                  value={formData.scoreNutrition}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="edit-scoreSommeil">Score Sommeil</Label>
                <Input
                  type="number"
                  min="0"
                  max="100"
                  name="scoreSommeil"
                  id="edit-scoreSommeil"
                  value={formData.scoreSommeil}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
            </ModalBody>
            <ModalFooter>
              <Button color="primary" type="submit">
                Sauvegarder
              </Button>
              <Button color="secondary" onClick={() => setEditModal(false)}>
                Annuler
              </Button>
            </ModalFooter>
          </Form>
        </Modal>
      </Container>
    </>
  );
};

export default ScoreSanteCRUD;
