import axios from "axios";

const API_URL = "http://localhost:5000/api/habitudes";

// ‚úÖ 1. GET_ALL_HABITUDES - R√©cup√©rer toutes les habitudes
export const getAllHabitudes = () => {
  console.log("üîç GET_ALL_HABITUDES - R√©cup√©ration de toutes les habitudes");
  return axios.get(API_URL);
};

// ‚úÖ 2. CREATE_HABITUDE - Cr√©er une nouvelle habitude
export const createHabitude = (habitudeData) => {
  console.log("‚ûï CREATE_HABITUDE - Cr√©ation d'une habitude:", habitudeData);
  return axios.post(API_URL, habitudeData);
};

// ‚úÖ 3. UPDATE_HABITUDE - Mettre √† jour une habitude existante
export const updateHabitude = (id, habitudeData) => {
  console.log("‚úèÔ∏è UPDATE_HABITUDE - Mise √† jour habitude ID:", id, habitudeData);
  return axios.put(`${API_URL}/${id}`, habitudeData);
};

// ‚úÖ 4. DELETE_HABITUDE - Supprimer une habitude
export const deleteHabitude = (id) => {
  console.log("üóëÔ∏è DELETE_HABITUDE - Suppression habitude ID:", id);
  return axios.delete(`${API_URL}/${id}`);
};
