import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios'; // ‚úÖ Import axios directement
import './SaifAIChat.css';

// Configuration API directe dans le composant
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Intercepteur pour le d√©bogage
api.interceptors.request.use(
  (config) => {
    console.log(`üîÑ API Call: ${config.method?.toUpperCase()} ${config.baseURL}${config.url}`);
    return config;
  },
  (error) => {
    console.error('‚ùå API Request Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API Response Success:', response.status);
    return response;
  },
  (error) => {
    console.error('‚ùå API Response Error:', error.response?.status, error.message);
    return Promise.reject(error);
  }
);

const SaifAIChat = () => {
  // ... le reste de votre code reste identique
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [context, setContext] = useState({ userId: 'user123' });
  const [systemStatus, setSystemStatus] = useState(null);
  const [connectionError, setConnectionError] = useState(false);
  const messagesEndRef = useRef(null);

  // Test de connexion au d√©marrage
  useEffect(() => {
    testConnection();
  }, []);


  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const testConnection = async () => {
    try {
      console.log('üîó Test de connexion au backend...');
      const response = await api.get('/api/test');
      console.log('‚úÖ Backend connect√©:', response.data);
      setConnectionError(false);
      checkSystemStatus();
    } catch (error) {
      console.error('‚ùå Impossible de se connecter au backend:', error);
      setConnectionError(true);
      
      // Message d'erreur initial
      setMessages([{
        id: Date.now(),
        type: 'error',
        content: {
          error: "Backend non connect√©",
          details: "V√©rifiez que le serveur backend est d√©marr√© sur http://localhost:3000"
        },
        timestamp: new Date().toISOString()
      }]);
    }
  };

  const checkSystemStatus = async () => {
    try {
      const response = await api.get('/api/saif-ai/statut');
      setSystemStatus(response.data);
      console.log('‚úÖ Statut Saif AI:', response.data);
    } catch (error) {
      console.error('‚ùå Erreur statut syst√®me:', error);
      setSystemStatus({ 
        statut: "üî¥ Hors ligne", 
        message: "Impossible de contacter Saif AI" 
      });
    }
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading || connectionError) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      console.log('üöÄ Envoi requ√™te Saif AI:', inputMessage);
      
      const response = await api.post('/api/saif-ai/executer', {
        prompt: inputMessage,
        context: context,
        executeQuery: true
      });

      console.log('‚úÖ R√©ponse Saif AI re√ßue:', response.data);

      const aiMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: response.data,
        timestamp: new Date().toISOString()
      };

      setMessages(prev => [...prev, aiMessage]);
      
      if (response.data.analyse?.entite) {
        setContext(prev => ({
          ...prev,
          lastEntity: response.data.analyse.entite
        }));
      }

    } catch (error) {
      console.error('‚ùå Erreur compl√®te:', error);
      
      const errorMessage = {
        id: Date.now() + 1,
        type: 'error',
        content: {
          error: "Erreur de communication avec Saif AI",
          details: error.response?.data?.error || error.message,
          status: error.response?.status,
          url: error.config?.url
        },
        timestamp: new Date().toISOString()
      };
      
      setMessages(prev => [...prev, errorMessage]);
      setConnectionError(true);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    setMessages([]);
    setConnectionError(false);
  };

  const quickAction = async (action) => {
    const quickPrompts = {
      sommeil: "Afficher mes habitudes de sommeil de cette semaine",
      nutrition: "Quelles sont mes consommations caloriques r√©centes ?",
      activit√©: "Montre-moi mes activit√©s physiques des derniers jours",
      analyse: "Analyse mes performances globales cette semaine",
      test: "Test de connexion backend"
    };

    if (action === 'test') {
      await testConnection();
      return;
    }

    setInputMessage(quickPrompts[action]);
    setTimeout(() => sendMessage(), 100);
  };

  return (
    <div className="saif-ai-chat">
      {/* En-t√™te avec indicateur de connexion */}
      <div className="chat-header">
        <div className="system-info">
          <h2>üß† Saif AI - Assistant Sant√© Intelligent</h2>
          <div className="connection-status">
            {connectionError ? (
              <span className="status offline">üî¥ D√©connect√©</span>
            ) : systemStatus ? (
              <span className={`status ${systemStatus.statut.includes('üü¢') ? 'online' : 'offline'}`}>
                {systemStatus.statut}
              </span>
            ) : (
              <span className="status checking">üü° V√©rification...</span>
            )}
          </div>
        </div>
        
        <div className="quick-actions">
          <button onClick={() => quickAction('test')} className="quick-btn test">
            üîß Test Connexion
          </button>
          <button onClick={() => quickAction('sommeil')} className="quick-btn sleep">
            üí§ Sommeil
          </button>
          <button onClick={() => quickAction('nutrition')} className="quick-btn nutrition">
            üçé Nutrition
          </button>
          <button onClick={() => quickAction('activit√©')} className="quick-btn activity">
            üèÉ Activit√©
          </button>
          <button onClick={() => quickAction('analyse')} className="quick-btn analysis">
            üìä Analyse
          </button>
        </div>
      </div>

      {/* Banni√®re d'erreur de connexion */}
      {connectionError && (
        <div className="connection-banner error">
          <div className="banner-content">
            <strong>‚ùå Backend non connect√©</strong>
            <p>Le serveur backend n'est pas accessible sur http://localhost:3000</p>
            <button onClick={testConnection} className="retry-btn">
              üîÑ R√©essayer
            </button>
          </div>
        </div>
      )}

      {/* Zone de conversation */}
      <div className="messages-container">
        {messages.length === 0 && !connectionError ? (
          <div className="welcome-message">
            <div className="welcome-content">
              <h3>Bienvenue dans Saif AI üß†</h3>
              <p>Je suis votre assistant sant√© intelligent. Je peux vous aider √† :</p>
              <ul>
                <li>üìù Cr√©er et g√©rer vos habitudes de sant√©</li>
                <li>üîç Analyser vos donn√©es de sommeil, nutrition et activit√©</li>
                <li>üìä G√©n√©rer des rapports et tendances</li>
                <li>üéØ Vous donner des recommandations personnalis√©es</li>
              </ul>
              <div className="setup-guide">
                <h4>üõ†Ô∏è Configuration requise :</h4>
                <ol>
                  <li>D√©marrez le serveur backend: <code>node backend/server.js</code></li>
                  <li>V√©rifiez que Fuseki est d√©marr√© sur http://localhost:3030</li>
                  <li>Cliquez sur "üîß Test Connexion" pour v√©rifier</li>
                </ol>
              </div>
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <MessageBubble key={message.id} message={message} />
          ))
        )}
        
        {isLoading && (
          <div className="message ai loading">
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
            <span className="thinking">Saif AI r√©fl√©chit...</span>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Zone de saisie */}
      <div className="input-container">
        <div className="input-wrapper">
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={connectionError 
              ? "Backend d√©connect√© - V√©rifiez la configuration..." 
              : "Parlez √† Saif AI... (ex: 'Afficher mes habitudes sommeil de cette semaine')"
            }
            rows="1"
            disabled={isLoading || connectionError}
          />
          <button 
            onClick={sendMessage} 
            disabled={!inputMessage.trim() || isLoading || connectionError}
            className="send-button"
          >
            {isLoading ? '‚è≥' : connectionError ? '‚ùå' : 'üöÄ'}
          </button>
        </div>
        
        <div className="chat-controls">
          <button onClick={clearChat} className="clear-btn">
            üóëÔ∏è Effacer
          </button>
          <button onClick={testConnection} className="status-btn">
            üîç V√©rifier Connexion
          </button>
        </div>
      </div>
    </div>
  );
};

// ... (le reste du code reste identique - MessageBubble, QueryResults, etc.)

// Composant de bulle de message
const MessageBubble = ({ message }) => {
  const renderContent = () => {
    if (message.type === 'error') {
      return (
        <div className="error-content">
          <strong>‚ùå Erreur:</strong> {message.content.error}
          {message.content.details && (
            <div className="error-details">{message.content.details}</div>
          )}
        </div>
      );
    }

    if (message.type === 'user') {
      return <div className="user-text">{message.content}</div>;
    }

    if (message.type === 'ai') {
      const data = message.content;
      return (
        <div className="ai-response">
          {/* Analyse de l'intention */}
          <div className="intent-section">
            <div className="intent-badge intent-{data.analyse.intention}">
              {data.analyse.intention.toUpperCase()}
            </div>
            <span className="entity-type">
              {data.analyse.entite?.type || 'Habitude'}
            </span>
          </div>

          {/* R√©sultats de la requ√™te */}
          {data.resultat && data.execute && (
            <div className="results-section">
              <h4>üìä R√©sultats:</h4>
              <QueryResults data={data.resultat} />
            </div>
          )}

          {/* Requ√™te SPARQL g√©n√©r√©e */}
          <div className="sparql-section">
            <details>
              <summary>üîç Voir la requ√™te SPARQL g√©n√©r√©e</summary>
              <pre className="sparql-query">{data.requeteGeneree}</pre>
            </details>
          </div>

          {/* Suggestions */}
          {data.suggestions && data.suggestions.length > 0 && (
            <div className="suggestions-section">
              <h5>üí° Suggestions:</h5>
              <ul>
                {data.suggestions.map((suggestion, index) => (
                  <li key={index}>{suggestion}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      );
    }
  };

  return (
    <div className={`message ${message.type}`}>
      <div className="message-avatar">
        {message.type === 'user' ? 'üë§' : 
         message.type === 'error' ? '‚ùå' : 'üß†'}
      </div>
      <div className="message-content">
        {renderContent()}
        <div className="message-time">
          {new Date(message.timestamp).toLocaleTimeString()}
        </div>
      </div>
    </div>
  );
};

// Composant d'affichage des r√©sultats de requ√™te
const QueryResults = ({ data }) => {
  if (!data || !data.results) {
    return <div className="no-results">Aucun r√©sultat trouv√©</div>;
  }

  const { results } = data;
  
  if (results.bindings.length === 0) {
    return <div className="no-results">Aucune donn√©e correspondante</div>;
  }

  const headers = results.head.vars;

  return (
    <div className="results-table">
      <table>
        <thead>
          <tr>
            {headers.map(header => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {results.bindings.map((row, index) => (
            <tr key={index}>
              {headers.map(header => (
                <td key={header}>
                  {row[header] ? row[header].value : 'N/A'}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="results-count">
        {results.bindings.length} r√©sultat(s) trouv√©(s)
      </div>
    </div>
  );
};

export default SaifAIChat;
