// Helper functions
const getStatusColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'planifiÃ©':
    case 'planifie':
      return "info";
    case 'en cours':
      return "warning";
    case 'terminÃ©':
    case 'termine':
    case 'complÃ©tÃ©':
    case 'complete':
      return "success";
    case 'annulÃ©':
    case 'annule':
      return "danger";
    default:
      return "secondary";
  }
};

const getStatusIcon = (status) => {
  switch (status?.toLowerCase()) {
    case 'planifiÃ©':
    case 'planifie':
      return "ðŸ“…";
    case 'en cours':
      return "ðŸ”„";
    case 'terminÃ©':
    case 'termine':
    case 'complÃ©tÃ©':
    case 'complete':
      return "âœ…";
    case 'annulÃ©':
    case 'annule':
      return "âŒ";
    default:
      return "ðŸ“‹";
  }
};

const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  try {
    return new Date(dateString).toLocaleDateString('fr-FR');
  } catch {
    return dateString;
  }
};

