import axios from "axios";

const API_URL = "http://localhost:5000/api/habitude-logs";

// ‚úÖ 1. GET_ALL_LOGS - R√©cup√©rer tous les logs
export const getAllLogs = () => {
  console.log("üîç GET_ALL_LOGS - R√©cup√©ration de tous les logs");
  return axios.get(API_URL);
};

// ‚úÖ 2. CREATE_LOG - Cr√©er un nouveau log
export const createLog = (logData) => {
  console.log("‚ûï CREATE_LOG - Cr√©ation d'un log:", logData);
  return axios.post(API_URL, logData);
};

// ‚úÖ 3. UPDATE_LOG - Mettre √† jour un log existant
export const updateLog = (id, logData) => {
  console.log("‚úèÔ∏è UPDATE_LOG - Mise √† jour log ID:", id, logData);
  return axios.put(`${API_URL}/${id}`, logData);
};

// ‚úÖ 4. DELETE_LOG - Supprimer un log
export const deleteLog = (id) => {
  console.log("üóëÔ∏è DELETE_LOG - Suppression log ID:", id);
  return axios.delete(`${API_URL}/${id}`);
};
