sourcesContent:
import { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Alert,
  Table,
  Badge,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  InputGroup,
  InputGroupText,
  ButtonGroup,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
} from "reactstrap";
import Header from "components/Headers/Header.js";

// Import with error handling for optional dependencies
let XLSX, jsPDF;
try {
  XLSX = require('xlsx');
  jsPDF = require('jspdf');
  require('jspdf-autotable');
} catch (error) {
  console.warn("Export dependencies not available:", error.message);
}

const EvenementForm = () => {
  const [formData, setFormData] = useState({
    psDateDebut: "",
    psDateFin: "",
    psStatus: "",
    psDescription: "",
    aTitle: "",
    aLocalisation: ""
  });
  const [alert, setAlert] = useState({ visible: false, message: "", color: "" });
  const [evenements, setEvenements] = useState([]);
  const [filteredEvenements, setFilteredEvenements] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [addModal, setAddModal] = useState(false);
  const [editingEvenement, setEditingEvenement] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  // AI States
  const [aiInput, setAiInput] = useState("");
  const [aiResponse, setAiResponse] = useState(null);
  const [isAiLoading, setIsAiLoading] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);

  // Stats & Export States
  const [stats, setStats] = useState(null);
  const [activeTab, setActiveTab] = useState('1');
  const [exportLoading, setExportLoading] = useState(false);
  const [statsLoading, setStatsLoading] = useState(false);

  useEffect(() => {
    fetchEvenements();
    fetchStats();
  }, []);

  useEffect(() => {
    filterEvenements();
  }, [searchTerm, evenements, sortConfig, activeFilters]);

  const fetchEvenements = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/evenements');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      console.log("ðŸ“¦ DonnÃ©es reÃ§ues:", result);
      setEvenements(result.results.bindings || []);
    } catch (error) {
      console.error("âŒ Erreur fetch:", error);
      showAlert("Erreur lors du chargement des Ã©vÃ©nements: " + error.message, "danger");
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    setStatsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/ai/stats/evenements');
      if (response.ok) {
        const statsData = await response.json();
        setStats(statsData);
      } else {
        // Use calculated stats from current data if API fails
        calculateStatsFromData();
      }
    } catch (error) {
      console.error("âŒ Erreur stats:", error);
      // Calculate stats from current data
      calculateStatsFromData();
    } finally {
      setStatsLoading(false);
    }
  };

  const calculateStatsFromData = () => {
    if (evenements.length === 0) {
      setStats({
        status: [],
        monthly: [],
        total: 0
      });
      return;
    }

    // Calculate status stats
    const statusCounts = {};
    evenements.forEach(event => {
      const status = event.psStatus?.value || 'Inconnu';
      statusCounts[status] = (statusCounts[status] || 0) + 1;
    });

    const statusStats = Object.entries(statusCounts).map(([status, count]) => ({
      status,
      count
    }));

    // Calculate monthly stats
    const monthCounts = {};
    evenements.forEach(event => {
      const dateStr = event.psDateDebut?.value;
      if (dateStr) {
        try {
          const date = new Date(dateStr);
          const month = date.getMonth() + 1; // 1-12
          monthCounts[month] = (monthCounts[month] || 0) + 1;
        } catch (e) {
          console.warn("Invalid date:", dateStr);
        }
      }
    });

    const monthlyStats = Object.entries(monthCounts).map(([month, count]) => ({
      month: parseInt(month),
      count
    })).sort((a, b) => a.month - b.month);

    setStats({
      status: statusStats,
      monthly: monthlyStats,
      total: evenements.length
    });
  };

  // Enhanced AI Processing with better context awareness
  const processAIQuestion = async (question) => {
    if (!question.trim()) return;

    setIsAiLoading(true);
    setAiResponse(null);

    try {
      // Get current events data to provide context to AI
      const eventsContext = evenements.map(event => ({
        title: event.aTitle?.value,
        location: event.aLocalisation?.value,
        status: event.psStatus?.value,
        startDate: event.psDateDebut?.value,
        endDate: event.psDateFin?.value,
        description: event.psDescription?.value
      }));

      const response = await fetch('http://localhost:5000/ai/process-evenements', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: question,
          context: {
            currentEvents: eventsContext,
            totalEvents: evenements.length,
            availableStatuses: ['PlanifiÃ©', 'En cours', 'TerminÃ©', 'AnnulÃ©']
          }
        })
      });

      if (!response.ok) {
        throw new Error(`AI service error: ${response.status}`);
      }

      const aiResult = await response.json();
      setAiResponse(aiResult);

      console.log("ðŸ¤– RÃ©sultat AI reÃ§u:", aiResult);

      // Handle different AI actions with better context
      switch (aiResult.action) {
        case 'create':
          showAlert("ðŸ¤– " + aiResult.natural_response, "success");
          setTimeout(() => executeAICreate(aiResult), 1000);
          break;

        case 'read':
          if (aiResult.filters && aiResult.filters.length > 0) {
            applyAIFilters(aiResult.filters);
            showAlert("ðŸŽ¯ " + aiResult.natural_response, "success");
          } else {
            // If no specific filters, show all events with search
            setSearchTerm(extractSearchTerms(question));
            showAlert(aiResult.natural_response, "info");
          }
          break;

        case 'update':
          showAlert("âœï¸ " + aiResult.natural_response, "success");
          setTimeout(() => executeAIUpdate(aiResult, question), 1000);
          break;

        case 'delete':
          showAlert("ðŸ—‘ï¸ " + aiResult.natural_response, "warning");
          setTimeout(() => executeAIDelete(aiResult), 1000);
          break;

        default:
          showAlert(aiResult.natural_response, "info");
      }

    } catch (error) {
      console.error("âŒ AI Error:", error);

      // Enhanced error handling with fallback
      if (error.message.includes('Failed to fetch') || error.message.includes('NetworkError')) {
        showAlert("ðŸ¤– Service AI temporairement indisponible. Utilisation du mode manuel.", "warning");
        // Fallback: perform smart search based on question
        handleSmartSearch(question);
      } else {
        showAlert("Erreur AI: " + error.message, "danger");
      }
    } finally {
      setIsAiLoading(false);
      setAiInput("");
    }
  };

  // Smart search fallback when AI is unavailable
  const handleSmartSearch = (question) => {
    const lowerQuestion = question.toLowerCase();

    // Detect status search
    if (lowerQuestion.includes('planifiÃ©') || lowerQuestion.includes('planifie')) {
      setActiveFilters([{ field: 'psStatus', operator: 'contains', value: 'PlanifiÃ©', description: 'Statut: PlanifiÃ©' }]);
      showAlert("ðŸ” Affichage des Ã©vÃ©nements planifiÃ©s", "info");
    } else if (lowerQuestion.includes('en cours')) {
      setActiveFilters([{ field: 'psStatus', operator: 'contains', value: 'En cours', description: 'Statut: En cours' }]);
      showAlert("ðŸ” Affichage des Ã©vÃ©nements en cours", "info");
    } else if (lowerQuestion.includes('terminÃ©') || lowerQuestion.includes('termine')) {
      setActiveFilters([{ field: 'psStatus', operator: 'contains', value: 'TerminÃ©', description: 'Statut: TerminÃ©' }]);
      showAlert("ðŸ” Affichage des Ã©vÃ©nements terminÃ©s", "info");
    } else if (lowerQuestion.includes('annulÃ©') || lowerQuestion.includes('annule')) {
      setActiveFilters([{ field: 'psStatus', operator: 'contains', value: 'AnnulÃ©', description: 'Statut: AnnulÃ©' }]);
      showAlert("ðŸ” Affichage des Ã©vÃ©nements annulÃ©s", "info");
    } else {
      // General search
      setSearchTerm(extractSearchTerms(question));
      showAlert(`ðŸ” Recherche: "${question}"`, "info");
    }
  };

  const extractSearchTerms = (question) => {
    // Remove common command words and extract meaningful terms
    const stopWords = ['affiche', 'montre', 'cherche', 'trouve', 'liste', 'vois', 'donne', 'les', 'des', 'Ã©vÃ©nements', 'events'];
    const words = question.toLowerCase().split(' ');
    return words.filter(word => !stopWords.includes(word) && word.length > 2).join(' ');
  };

  // Enhanced AI Create with validation
  const executeAICreate = async (aiResult) => {
    try {
      const eventData = {
        psDateDebut: aiResult.data?.psDateDebut || new Date().toISOString().split('T')[0],
        psDateFin: aiResult.data?.psDateFin || new Date(Date.now() + 86400000).toISOString().split('T')[0],
        psStatus: aiResult.data?.psStatus || "PlanifiÃ©",
        psDescription: aiResult.data?.psDescription || "CrÃ©Ã© automatiquement via AI",
        aTitle: aiResult.data?.aTitle || "Nouvel Ã‰vÃ©nement AI",
        aLocalisation: aiResult.data?.aLocalisation || "Non spÃ©cifiÃ©"
      };

      // Validate required fields
      if (!eventData.aTitle || !eventData.aLocalisation) {
        throw new Error('DonnÃ©es AI incomplÃ¨tes pour la crÃ©ation');
      }

      const response = await fetch('http://localhost:5000/api/evenements', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(eventData)
      });

      if (response.ok) {
        showAlert("âœ… Ã‰vÃ©nement crÃ©Ã© automatiquement avec succÃ¨s!", "success");
        fetchEvenements();
        fetchStats(); // Refresh stats
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erreur lors de la crÃ©ation automatique');
      }
    } catch (error) {
      showAlert("âŒ Erreur crÃ©ation automatique: " + error.message, "danger");
    }
  };

  // Enhanced AI Update
  const executeAIUpdate = async (aiResult, originalQuestion) => {
    try {
      // Find event to update based on AI filters or use first filtered event
      let evenementToEdit = null;

      if (filteredEvenements.length > 0) {
        evenementToEdit = filteredEvenements[0];
      } else if (aiResult.filters && aiResult.filters.length > 0) {
        // Apply filters temporarily to find the event
        const tempFiltered = evenements.filter(evenement => 
          aiResult.filters.every(filter => 
            checkAIFilter(evenement, filter)
          )
        );
        evenementToEdit = tempFiltered[0];
      } else {
        evenementToEdit = evenements[0];
      }

      if (evenementToEdit) {
        const evenementId = evenementToEdit.evenement.value.split('#')[1];

        const updateData = {
          psDateDebut: aiResult.data?.psDateDebut || evenementToEdit.psDateDebut?.value,
          psDateFin: aiResult.data?.psDateFin || evenementToEdit.psDateFin?.value,
          psStatus: aiResult.data?.psStatus || evenementToEdit.psStatus?.value,
          psDescription: aiResult.data?.psDescription || evenementToEdit.psDescription?.value,
          aTitle: aiResult.data?.aTitle || evenementToEdit.aTitle?.value,
          aLocalisation: aiResult.data?.aLocalisation || evenementToEdit.aLocalisation?.value
        };

        const response = await fetch(`http://localhost:5000/api/evenements/${evenementId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updateData)
        });

        if (response.ok) {
          showAlert(`âœ… Ã‰vÃ©nement modifiÃ© automatiquement!`, "success");
          fetchEvenements();
          fetchStats();
        } else {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Erreur lors de la modification automatique');
        }
      } else {
        showAlert("âŒ Aucun Ã©vÃ©nement trouvÃ© pour modification automatique", "warning");
      }
    } catch (error) {
      showAlert("âŒ Erreur Ã©dition automatique: " + error.message, "danger");
    }
  };

  // Enhanced AI Delete
  const executeAIDelete = async (aiResult) => {
    try {
      let eventsToDelete = [];

      if (aiResult.filters && aiResult.filters.length > 0) {
        eventsToDelete = evenements.filter(evenement => 
          aiResult.filters.every(filter => checkAIFilter(evenement, filter))
        );
      } else if (filteredEvenements.length > 0) {
        eventsToDelete = filteredEvenements;
      } else {
        eventsToDelete = [evenements[0]];
      }

      if (eventsToDelete.length === 0) {
        showAlert("âŒ Aucun Ã©vÃ©nement Ã  supprimer trouvÃ©", "warning");
        return;
      }

      // Ask for confirmation before bulk delete
      if (eventsToDelete.length > 1) {
        if (!window.confirm(`ÃŠtes-vous sÃ»r de vouloir supprimer ${eventsToDelete.length} Ã©vÃ©nements ?`)) {
          return;
        }
      }

      let deletedCount = 0;
      for (const evenement of eventsToDelete) {
        const evenementId = evenement.evenement.value.split('#')[1];
        const response = await fetch(`http://localhost:5000/api/evenements/${evenementId}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          deletedCount++;
        }
      }

      showAlert(`âœ… ${deletedCount} Ã©vÃ©nement(s) supprimÃ©(s) automatiquement!`, "success");
      fetchEvenements();
      fetchStats();

    } catch (error) {
      showAlert("âŒ Erreur suppression automatique: " + error.message, "danger");
    }
  };

  const checkAIFilter = (evenement, filter) => {
    const value = evenement[filter.field]?.value;
    if (!value) return false;

    switch (filter.operator) {
      case 'contains':
        return value.toLowerCase().includes(filter.value.toLowerCase());
      case '==':
        return value === filter.value;
      default:
        return true;
    }
  };

  const applyAIFilters = (filters) => {
    setActiveFilters(filters);
    const filterDescriptions = filters.map(f => f.description || `${f.field} ${f.operator} ${f.value}`);
    showAlert(`ðŸŽ¯ Filtres AI appliquÃ©s: ${filterDescriptions.join(', ')}`, "success");
  };

  // Fixed PDF Export Function
  const exportToPDF = async () => {
    if (!jsPDF) {
      showAlert("âŒ FonctionnalitÃ© PDF non disponible. Installez jspdf et jspdf-autotable.", "warning");
      return;
    }

    setExportLoading(true);
    try {
      const doc = new jsPDF();

      // Title
      doc.setFontSize(16);
      doc.text('Rapport des Ã‰vÃ©nements', 14, 15);
      doc.setFontSize(10);
      doc.text(`GÃ©nÃ©rÃ© le ${new Date().toLocaleDateString('fr-FR')}`, 14, 22);

      // Stats summary
      let startY = 32;
      if (stats && stats.total > 0) {
        doc.text(`Total Ã©vÃ©nements: ${stats.total}`, 14, startY);
        startY += 8;

        // Status summary
        doc.setFontSize(12);
        doc.text('RÃ©partition par statut:', 14, startY);
        startY += 6;
        doc.setFontSize(10);

        stats.status.forEach(stat => {
          doc.text(`â€¢ ${stat.status}: ${stat.count}`, 20, startY);
          startY += 5;
        });
        startY += 10;
      }

      // Table data preparation
      const tableData = filteredEvenements.map(event => [
        event.evenement?.value.split('#')[1] || '',
        event.aTitle?.value || 'N/A',
        event.aLocalisation?.value || 'N/A',
        formatDate(event.psDateDebut?.value),
        formatDate(event.psDateFin?.value),
        event.psStatus?.value || 'N/A'
      ]);

      // Add table
      doc.autoTable({
        head: [['ID', 'Titre', 'Localisation', 'Date DÃ©but', 'Date Fin', 'Statut']],
        body: tableData,
        startY: startY,
        styles: { fontSize: 8, cellPadding: 2 },
        headStyles: { 
          fillColor: [41, 128, 185],
          textColor: 255,
          fontStyle: 'bold'
        },
        alternateRowStyles: {
          fillColor: [245, 245, 245]
        },
        margin: { top: startY }
      });

      doc.save(`evenements_${new Date().toISOString().split('T')[0]}.pdf`);
      showAlert("âœ… Fichier PDF exportÃ© avec succÃ¨s!", "success");
    } catch (error) {
      console.error("PDF Export Error:", error);
      showAlert("âŒ Erreur lors de l'export PDF: " + error.message, "danger");
    } finally {
      setExportLoading(false);
    }
  };

  // Export to Excel function
  const exportToExcel = async () => {
    if (!XLSX) {
      showAlert("âŒ FonctionnalitÃ© Excel non disponible. Installez xlsx.", "warning");
      return;
    }

    setExportLoading(true);
    try {
      const data = filteredEvenements.map(event => ({
        ID: event.evenement?.value.split('#')[1] || '',
        Titre: event.aTitle?.value || '',
        Localisation: event.aLocalisation?.value || '',
        'Date DÃ©but': formatDate(event.psDateDebut?.value),
        'Date Fin': formatDate(event.psDateFin?.value),
        Statut: event.psStatus?.value || '',
        Description: event.psDescription?.value || ''
      }));

      const ws = XLSX.utils.json_to_sheet(data);
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, "Ã‰vÃ©nements");
      XLSX.writeFile(wb, `evenements_${new Date().toISOString().split('T')[0]}.xlsx`);
      showAlert("âœ… Fichier Excel exportÃ© avec succÃ¨s!", "success");
    } catch (error) {
      showAlert("âŒ Erreur lors de l'export Excel: " + error.message, "danger");
    } finally {
      setExportLoading(false);
    }
  };

  // Rest of your CRUD operations remain the same...
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/evenements', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          psDateDebut: formData.psDateDebut,
          psDateFin: formData.psDateFin,
          psStatus: formData.psStatus,
          psDescription: formData.psDescription,
          aTitle: formData.aTitle,
          aLocalisation: formData.aLocalisation
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erreur lors de l\'insertion');
      }

      const result = await response.json();
      showAlert(result.message || "Ã‰vÃ©nement ajoutÃ© avec succÃ¨s!", "success");
      setFormData({ 
        psDateDebut: "", 
        psDateFin: "", 
        psStatus: "", 
        psDescription: "", 
        aTitle: "", 
        aLocalisation: "" 
      });
      setAddModal(false);
      fetchEvenements();
      fetchStats();
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    try {
      const evenementId = editingEvenement.evenement.value.split('#')[1];
      const response = await fetch(`http://localhost:5000/api/evenements/${evenementId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          psDateDebut: formData.psDateDebut,
          psDateFin: formData.psDateFin,
          psStatus: formData.psStatus,
          psDescription: formData.psDescription,
          aTitle: formData.aTitle,
          aLocalisation: formData.aLocalisation
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erreur lors de la modification');
      }

      const result = await response.json();
      showAlert(result.message || "Ã‰vÃ©nement modifiÃ© avec succÃ¨s!", "success");
      setEditModal(false);
      setEditingEvenement(null);
      setFormData({ 
        psDateDebut: "", 
        psDateFin: "", 
        psStatus: "", 
        psDescription: "", 
        aTitle: "", 
        aLocalisation: "" 
      });
      fetchEvenements();
      fetchStats();
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  const handleDelete = async (evenementUri) => {
    if (window.confirm("ÃŠtes-vous sÃ»r de vouloir supprimer cet Ã©vÃ©nement ?")) {
      try {
        const evenementId = evenementUri.split('#')[1];
        const response = await fetch(`http://localhost:5000/api/evenements/${evenementId}`, {
          method: 'DELETE'
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Erreur lors de la suppression');
        }

        const result = await response.json();
        showAlert(result.message || "Ã‰vÃ©nement supprimÃ© avec succÃ¨s!", "success");
        fetchEvenements();
        fetchStats();
      } catch (error) {
        showAlert("Erreur: " + error.message, "danger");
      }
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filterEvenements = () => {
    let filtered = evenements.filter(evenement => {
      const searchLower = searchTerm.toLowerCase();
      const fields = [
        evenement.psDateDebut?.value || '',
        evenement.psDateFin?.value || '',
        evenement.psStatus?.value || '',
        evenement.psDescription?.value || '',
        evenement.aTitle?.value || '',
        evenement.aLocalisation?.value || '',
        evenement.evenement?.value.split('#')[1] || ''
      ];

      const searchMatch = fields.some(field => 
        field.toString().toLowerCase().includes(searchLower)
      );

      const aiFilterMatch = activeFilters.length === 0 ? true : 
        activeFilters.every(filter => checkAIFilter(evenement, filter));

      return searchMatch && aiFilterMatch;
    });

    if (sortConfig.key) {
      filtered.sort((a, b) => {
        let aValue = getSortValue(a, sortConfig.key);
        let bValue = getSortValue(b, sortConfig.key);

        if (aValue < bValue) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }

    setFilteredEvenements(filtered);
  };

  const getSortValue = (evenement, key) => {
    switch (key) {
      case 'psDateDebut':
        return evenement.psDateDebut?.value || '';
      case 'psDateFin':
        return evenement.psDateFin?.value || '';
      case 'psStatus':
        return evenement.psStatus?.value || '';
      case 'aTitle':
        return evenement.aTitle?.value || '';
      case 'aLocalisation':
