                <Row className="align-items-center">
                  <Col>
                    <h3 className="mb-0">â­ Gestion des Scores SantÃ©</h3>
                    {activeFilters.length > 0 && (
                      <small className="text-muted">
                        Affichage filtrÃ© par AI ({filteredScores.length} rÃ©sultat(s))
                      </small>
                    )}
                  </Col>
                  <Col className="text-right">
                    <Button 
                      color="primary" 
