                                onClick={() => handleSort('sommeil', 'desc')}
                                size="sm"
                              >
                                â†“
                              </Button>
                            </ButtonGroup>
                          </div>
                        </th>
                        <th className="border-0 text-center">âš™ï¸ Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredScores.length === 0 ? (
