import { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Alert,
  Table,
  Badge,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";
import Header from "components/Headers/Header.js";

const MesureCRUD = () => {
  const [formData, setFormData] = useState({
    valeurIMC: "",
    caloriesConsommees: "",
    mesureValue: ""
  });
  const [alert, setAlert] = useState({ visible: false, message: "", color: "" });
  const [mesures, setMesures] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [editingMesure, setEditingMesure] = useState(null);

  useEffect(() => {
    fetchMesures();
  }, []);

  const fetchMesures = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/mesures');
      const result = await response.json();
      setMesures(result.results.bindings);
    } catch (error) {
      showAlert("Erreur lors du chargement des mesures", "danger");
    } finally {
      setLoading(false);
    }
  };

  const showAlert = (message, color) => {
    setAlert({ visible: true, message, color });
    setTimeout(() => setAlert({ ...alert, visible: false }), 5000);
  };

  const onDismiss = () => setAlert({ ...alert, visible: false });

  // CREATE
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/mesures', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          valeurIMC: formData.valeurIMC,
          caloriesConsommees: formData.caloriesConsommees,
          mesureValue: formData.mesureValue
        })
      });

      if (response.ok) {
        showAlert("Mesure ajoutée avec succès!", "success");
        setFormData({ valeurIMC: "", caloriesConsommees: "", mesureValue: "" });
        fetchMesures();
      } else {
        throw new Error('Erreur lors de l\'insertion');
      }
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  // UPDATE - Ouvrir modal d'édition
  const openEditModal = (mesure) => {
    setEditingMesure(mesure);
    setFormData({
      valeurIMC: mesure.imc?.value || "",
      caloriesConsommees: mesure.calories?.value || "",
      mesureValue: mesure.mesureValue?.value || ""
    });
    setEditModal(true);
  };

  // UPDATE - Sauvegarder
  const handleUpdate = async (e) => {
    e.preventDefault();
    
    try {
      const mesureId = editingMesure.mesure.value.split('#')[1];
      const response = await fetch(`http://localhost:5000/api/mesures/${mesureId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          valeurIMC: formData.valeurIMC,
          caloriesConsommees: formData.caloriesConsommees,
          mesureValue: formData.mesureValue
        })
      });

      if (response.ok) {
        showAlert("Mesure modifiée avec succès!", "success");
        setEditModal(false);
        setEditingMesure(null);
        setFormData({ valeurIMC: "", caloriesConsommees: "", mesureValue: "" });
        fetchMesures();
      } else {
        throw new Error('Erreur lors de la modification');
      }
    } catch (error) {
      showAlert("Erreur: " + error.message, "danger");
    }
  };

  // DELETE
  const handleDelete = async (mesureUri) => {
    if (window.confirm("Êtes-vous sûr de vouloir supprimer cette mesure ?")) {
      try {
        const mesureId = mesureUri.split('#')[1];
        const response = await fetch(`http://localhost:5000/api/mesures/${mesureId}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          showAlert("Mesure supprimée avec succès!", "success");
          fetchMesures();
        } else {
          throw new Error('Erreur lors de la suppression');
        }
      } catch (error) {
        showAlert("Erreur: " + error.message, "danger");
      }
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const getIMCCategory = (imc) => {
    if (!imc) return { label: "Non défini", color: "secondary" };
    const imcValue = parseFloat(imc);
    if (imcValue < 18.5) return { label: "Maigreur", color: "warning" };
    if (imcValue < 25) return { label: "Normal", color: "success" };
    if (imcValue < 30) return { label: "Surpoids", color: "warning" };
    return { label: "Obésité", color: "danger" };
  };

  return (
    <>
      <Header />
      <Container className="mt--7" fluid>
        <Row>
          <Col lg="6">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <h3 className="mb-0">Gestion des Mesures</h3>
              </CardHeader>
              <CardBody>
                {alert.visible && (
                  <Alert color={alert.color} toggle={onDismiss}>
                    {alert.message}
                  </Alert>
                )}
                
                <Form onSubmit={handleSubmit}>
                  <FormGroup>
                    <Label for="valeurIMC">IMC</Label>
                    <Input
                      type="number"
                      step="0.1"
                      name="valeurIMC"
                      id="valeurIMC"
                      placeholder="Ex: 24.5"
                      value={formData.valeurIMC}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="caloriesConsommees">Calories Consommées</Label>
                    <Input
                      type="number"
                      name="caloriesConsommees"
                      id="caloriesConsommees"
                      placeholder="Ex: 2000"
                      value={formData.caloriesConsommees}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="mesureValue">Mesure (Pas ou autre)</Label>
                    <Input
                      type="number"
                      name="mesureValue"
                      id="mesureValue"
                      placeholder="Ex: 75"
                      value={formData.mesureValue}
                      onChange={handleChange}
                      required
                    />
                  </FormGroup>
                  <Button color="primary" type="submit">
                    Ajouter la Mesure
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>

          {/* TABLEAU DES MESURES */}
          <Col lg="6">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <h3 className="mb-0">Liste des Mesures</h3>
                <Button color="primary" size="sm" onClick={fetchMesures}>
                  Actualiser
                </Button>
              </CardHeader>
              <CardBody>
                {loading ? (
                  <div>Chargement...</div>
                ) : (
                  <Table responsive>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>IMC</th>
                        <th>Calories</th>
                        <th>Mesure</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {mesures.map((mesure, index) => {
                        const imcCategory = getIMCCategory(mesure.imc?.value);
                        return (
                          <tr key={index}>
                            <td>{mesure.mesure.value.split('#')[1]}</td>
                            <td>
                              <Badge color={imcCategory.color}>
                                {mesure.imc?.value || 'N/A'} - {imcCategory.label}
                              </Badge>
                            </td>
                            <td>{mesure.calories?.value || 'N/A'}</td>
                            <td>{mesure.mesureValue?.value || 'N/A'}</td>
                            <td>
                              <Button 
                                color="warning" 
                                size="sm" 
                                onClick={() => openEditModal(mesure)}
                              >
                                Modifier
                              </Button>
                              {' '}
                              <Button 
                                color="danger" 
                                size="sm" 
                                onClick={() => handleDelete(mesure.mesure.value)}
                              >
                                Supprimer
                              </Button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                )}
              </CardBody>
            </Card>
          </Col>
        </Row>

        {/* MODAL D'ÉDITION */}
        <Modal isOpen={editModal} toggle={() => setEditModal(!editModal)}>
          <ModalHeader toggle={() => setEditModal(!editModal)}>
            Modifier la Mesure
          </ModalHeader>
          <Form onSubmit={handleUpdate}>
            <ModalBody>
              <FormGroup>
                <Label for="edit-valeurIMC">IMC</Label>
                <Input
                  type="number"
                  step="0.1"
                  name="valeurIMC"
                  id="edit-valeurIMC"
                  value={formData.valeurIMC}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="edit-caloriesConsommees">Calories Consommées</Label>
                <Input
                  type="number"
                  name="caloriesConsommees"
                  id="edit-caloriesConsommees"
                  value={formData.caloriesConsommees}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="edit-mesureValue">Mesure</Label>
                <Input
                  type="number"
                  name="mesureValue"
                  id="edit-mesureValue"
                  value={formData.mesureValue}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
            </ModalBody>
            <ModalFooter>
              <Button color="primary" type="submit">
                Sauvegarder
              </Button>
              <Button color="secondary" onClick={() => setEditModal(false)}>
                Annuler
              </Button>
            </ModalFooter>
          </Form>
        </Modal>
      </Container>
    </>
  );
};

export default MesureCRUD;
